mapper.select:
select

from biz_user as a
where
a.del_flag=0

	and a.id=#{id}


	and a.login_name=#{loginName}

select count(*) from biz_user as a
where
1=1

	and a.id=#{id}


	and a.login_name=#{loginName}

and a.del_flag=0

select
,a.password as password from biz_user as a where a.del_flag=0
and a.id=#{id}
and a.login_name=#{loginName}
